    # Remove 'https://' or 'http://'
    head = ''
    if url.startswith('https://'):
        head = 'https://'
        url = url[8:]
    elif url.startswith('http://'):
        head = 'http://'
        url = url[7:]
    index = url.rfind('/')
    if index != -1:
        index2 = url.rfind('#')
        if index2 > index:
            url = url[:index2]
    return head + url

