k_dun = Bytes("dun")
k_dun_no = Bytes("dun_no")
k_parliament = Bytes("parliament")
k_parliament_no = Bytes("parliament_no")
k_state = Bytes("state")
k_c_name = Bytes("c_name")
k_party = Bytes("party")

router = Router("voting_area_initialisation", BareCallActions(no_op=OnCompleteAction.create_only(Approve()), opt_in=OnCompleteAction.call_only(Approve())))

@router.method
def init_dun(dun: abi.String, n: abi.Uint8, state: abi.String):
    check = And(Len(dun.get()) <= Int(20), n.get() > Int(0), n.get() < Int(83), Len(state.get()) <= Int(15))
    return If(check, Seq(App.localPut(Txn.sender(), k_dun, dun.get()), App.localPut(Txn.sender(), k_dun_no, n.get()), App.localPut(Txn.sender(), k_state, state.get())))

@router.method
def init_parliament(parliamen: abi.String, n: abi.Uint8, state: abi.String):
    check = And(Len(parliamen.get()) <= Int(20), n.get() > Int(0), n.get() < Int(223), Len(state.get()) <= Int(15))
    return If(check, Seq(App.localPut(Txn.sender(), k_parliament, parliamen.get()), App.localPut(Txn.sender(), k_parliament_no, n.get()), App.localPut(Txn.sender(), k_state, state.get())))

@router.method
def init_candidate(name: abi.String, party: abi.String):
    check = And(Len(name.get()) <= Int(40), Len(party.get()) <= Int(20))
    return If(check, Seq(App.localPut(Txn.sender(), k_c_name, name.get()), App.localPut(Txn.sender(), k_party, party.get())))

@router.method
def read_dun(*, output: abi.String):
    return output.set(App.localGet(Txn.sender(), k_dun))

@router.method
