def _execute_deploy_command(  # noqa: PLR0913
    *,
    path: Path,
    environment_name: str | None,
    command: list[str] | None,
    interactive: bool,
    deployer_alias: str | None,
    dispenser_alias: str | None,
    extra_args: tuple[str, ...],
) -> None:
    logger.debug(f"Deploying from project directory: {path}")
    logger.debug("Loading deploy command from project config")
    config = load_deploy_config(name=environment_name, project_dir=path)
    if command:
        config.command = command
    elif not config.command:
        if environment_name is None:
            msg = f"No generic deploy command specified in '{ALGOKIT_CONFIG}' file."
        else:
            msg = (
                f"Deploy command for '{environment_name}' is not specified in '{ALGOKIT_CONFIG}' file, "
                "and no generic command available."
            )
        raise click.ClickException(msg)
    resolved_command = resolve_command_path(config.command + list(extra_args))
    logger.info(f"Using deploy command: {' '.join(resolved_command)}")
    logger.info("Loading deployment environment variables...")
    config_dotenv = load_deploy_env_files(environment_name, path)
    # environment variables take precedence over those in .env* files
    config_env = {**{k: v for k, v in config_dotenv.items() if v is not None}, **os.environ}
    _ensure_aliases(config_env, deployer_alias=deployer_alias, dispenser_alias=dispenser_alias)

    if config.environment_secrets:
        _ensure_environment_secrets(
            config_env,
            config.environment_secrets,
            skip_mnemonics_prompts=not interactive,
        )
    logger.info("Deploying smart contracts from AlgoKit compliant repository 🚀")
    try:
        result = proc.run(resolved_command, cwd=path, env=config_env, stdout_log_level=logging.INFO)
    except FileNotFoundError as ex:
        raise click.ClickException(f"Failed to execute deploy command, '{resolved_command[0]}' wasn't found") from ex
    except PermissionError as ex:
        raise click.ClickException(
            f"Failed to execute deploy command '{resolved_command[0]}', permission denied"
        ) from ex
    else:
        if result.exit_code != 0:
            raise click.ClickException(f"Deployment command exited with error code = {result.exit_code}")

class _CommandParamType(click.types.StringParamType):
    name = "command"

    def convert(
        self,
        value: t.Any,  # noqa: ANN401
        param: click.Parameter | None,
        ctx: click.Context | None,
    ) -> list[str]:
        str_value = super().convert(value=value, param=param, ctx=ctx)
        try:
            return split_command_string(str_value)
        except ValueError as ex:
            logger.debug(f"Failed to parse command string: {str_value}", exc_info=True)
            raise click.BadParameter(str(ex), param=param, ctx=ctx) from ex

class _DeployCommand(click.Command):
    def parse_args(self, ctx: click.Context, args: list[str]) -> list[str]:
        # Join all args into a single string
        full_command = " ".join(args)

        try:
            separator_index = full_command.find("-- ")
            if separator_index == -1:
                raise ValueError("No separator found")
            main_args = args[:separator_index]
            extra_args = args[separator_index + 1 :]
        except Exception:
            main_args = args
            extra_args = []

        # Ensure we have at least one argument for environment_name if extra_args exist
        if extra_args and len(main_args) == 0:
            main_args.insert(0, "")

        # Reconstruct args list
        args = main_args + (["--"] if extra_args else []) + extra_args

        return super().parse_args(ctx, args)

def _prompt_contract_project() -> ContractArtifacts | None:
    """Prompts the user to select a contract project.

    Returns:
        ContractArtifacts | None: The selected contract project artifacts or None if no projects are available.
    """
    contract_projects = _get_contract_projects()

    if not contract_projects:
        return None

    return typing.cast(
        ContractArtifacts,
        questionary_extensions.prompt_select(
            "Select contract project to link with",
            *[questionary.Choice(title=contract.project_name, value=contract) for contract in contract_projects],
        ),
    )

def _select_contract_projects_to_link(
    *,
    project_names: typing.Sequence[str] | None = None,
    link_all: bool = False,
) -> list[ContractArtifacts]:
    """Selects contract projects to link based on criteria.

    Args:
        project_names (typing.Sequence[str] | None): Specific project names to link. Defaults to None.
        link_all (bool): Whether to link all projects. Defaults to False.

    Returns:
        list[ContractArtifacts]: A list of contract project artifacts to link.
    """
    if link_all:
        return _get_contract_projects()
    elif project_names:
        return [project for project in _get_contract_projects() if project.project_name in project_names]
    else:
        contract_project = _prompt_contract_project()
        return [contract_project] if contract_project else []

def _is_workspace(workspace_path: Path | None = None) -> bool:
    config = get_algokit_config(project_dir=get_workspace_project_path(workspace_path)) or {}
    project = config.get("project", {})
    return bool(project.get("type", None) == ProjectType.WORKSPACE)

def get_input_files(*, input_paths: tuple[Path], recursive: bool) -> list[Path]:
    """
    Get input files based on the input paths and recursive flag.

    Args:
        input_paths (tuple[Path]): Tuple of input paths.
        recursive (bool): Flag to indicate recursive search.

    Returns:
        list[Path]: List of input files.
    """

    input_files = []
    for input_path in input_paths:
        if input_path.is_dir():
            pattern = "**/*.teal" if recursive else "*.teal"
            input_files.extend(sorted(input_path.glob(pattern)))
        else:
            if recursive:
                click.secho(
                    f"Warning: Ignoring recursive flag for {input_path} as it is not a directory.\n",
                    fg="yellow",
                )
            input_files.append(input_path)
    return sorted(set(input_files))

def _get_zero_balanced_assets(
    *, provided_asset_ids: tuple[int], address: str, algod_client: AlgodClient, all_assets: bool = False
) -> list[int]:
    asset_ids_list = []
    if all_assets:
        account_info = get_account_info(algod_client, address)
        for asset in account_info.get("assets", []):
            if asset.get("amount", 0) == 0:
                asset_ids_list.append(int(asset["asset-id"]))
    else:
        for asset_id in provided_asset_ids:
            asset_ids_list.append(asset_id)

    return asset_ids_list

def opt_in_command(asset_ids: tuple[int], account: str, network: AlgorandNetwork) -> None:
    asset_ids_list = list(asset_ids)

    opt_in_account = get_account_with_private_key(account)
    validate_address(opt_in_account.address)
    algod_client = load_algod_client(network)
    algorand = get_algorand_client_for_network(network)

    validate_account_balance_to_opt_in(algod_client, opt_in_account, len(asset_ids_list))
    try:
        click.echo("Performing opt-in. This may take a few seconds...")
        response = algorand.asset.bulk_opt_in(
            account=opt_in_account.address,
            asset_ids=asset_ids_list,
            signer=opt_in_account.signer,
        )
        click.echo("Successfully performed opt-in.")
        if len(response) > 1:
            account_url = get_explorer_url(opt_in_account.address, network, ExplorerEntityType.ADDRESS)
            click.echo(f"Check latest transactions on your account at: {account_url}")
        else:
            for asset_opt_int_result in response:
                explorer_url = get_explorer_url(asset_opt_int_result.transaction_id, network, ExplorerEntityType.ASSET)
                click.echo(f"Check opt-in status for asset {asset_opt_int_result.asset_id} at: {explorer_url}")
    except error.AlgodHTTPError as err:
        raise click.ClickException(str(err)) from err
    except ValueError as err:
        logger.debug(err, exc_info=True)
        raise click.ClickException(str(err)) from err
    except Exception as err:
        logger.debug(err, exc_info=True)
        raise click.ClickException("Failed to perform opt-in") from err

def opt_out_command(*, asset_ids: tuple[int], account: str, network: AlgorandNetwork, all_assets: bool) -> None:
    if not (all_assets or asset_ids):
        raise click.UsageError("asset_ids or --all must be specified")
    opt_out_account = get_account_with_private_key(account)
    validate_address(opt_out_account.address)
    algod_client = load_algod_client(network)
    algorand = get_algorand_client_for_network(network)
    asset_ids_list = []
    try:
        asset_ids_list = _get_zero_balanced_assets(
            provided_asset_ids=asset_ids,
            address=opt_out_account.address,
            algod_client=algod_client,
            all_assets=all_assets,
        )

        if not asset_ids_list:
            raise click.ClickException("No assets to opt-out of.")

        click.echo("Performing opt-out. This may take a few seconds...")
        response = algorand.asset.bulk_opt_out(
            account=opt_out_account.address,
            asset_ids=asset_ids_list,
            signer=opt_out_account.signer,
        )
        click.echo("Successfully performed opt-out.")
        if len(response) > 1:
            account_url = get_explorer_url(opt_out_account.address, network, ExplorerEntityType.ADDRESS)
            click.echo(f"Check latest transactions on your account at: {account_url}")
        else:
            asset_opt_out_result = response[0]
            transaction_url = get_explorer_url(
                asset_opt_out_result.transaction_id, network, ExplorerEntityType.TRANSACTION
            )
            click.echo(f"Check opt-in status for asset {asset_opt_out_result.asset_id} at: {transaction_url}")
    except error.AlgodHTTPError as err:
        raise click.ClickException(str(err)) from err
    except ConnectionRefusedError as err:
        raise click.ClickException(str(err)) from err
    except ValueError as err:
        logger.debug(err, exc_info=True)
        raise click.ClickException(str(err)) from err
    except Exception as err:
        logger.debug(err, exc_info=True)
        raise click.ClickException("Failed to perform opt-out.") from err

def upload(file_path: Path, name: str | None) -> None:
    pinata_jwt = get_pinata_jwt()
    if not pinata_jwt:
        raise click.ClickException("You are not logged in! Please login using `algokit task ipfs login`.")

    try:
        total = file_path.stat().st_size
        if total > MAX_FILE_SIZE:
            raise click.ClickException("File size exceeds 100MB limit!")

        def upload() -> str:
            return upload_to_pinata(file_path, pinata_jwt, name)

        cid = run_with_animation(
            target_function=upload,
            animation_text="Uploading",
        )
        logger.info(f"File uploaded successfully!\n CID: {cid}")

    except click.ClickException as ex:
        raise ex
    except OSError as ex:
        logger.debug(ex)
        raise click.ClickException("Failed to open file!") from ex
    except (
        PinataBadRequestError,
        PinataUnauthorizedError,
        PinataForbiddenError,
        PinataInternalServerError,
        PinataHttpError,
    ) as ex:
        logger.debug(ex)
        raise click.ClickException(repr(ex)) from ex
    except Exception as ex:
        logger.debug(ex)
        raise click.ClickException("Failed to upload file!") from ex

def _get_and_validate_asset_name(context: click.Context, param: click.Parameter, value: str | None) -> str:
    """
    Validate the asset name by checking if its byte length is less than or equal to a predefined maximum value.
    If asset name has not been supplied in the metadata file or via an argument a prompt is displayed.

    Args:
        context (click.Context): The click context.
        param (click.Parameter): The click parameter.
        value (str|None): The value of the parameter.

    Returns:
        str: The value of the parameter if it passes the validation.
    """
    token_metadata_path = context.params.get("token_metadata_path")
    token_name = None

    if token_metadata_path is not None:
        with Path(token_metadata_path).open(mode="r", encoding="utf-8") as metadata_file:
            data = json.load(metadata_file)
            token_name = data.get("name")

    if value is None:
        if token_name is None:
            value = click.prompt("Provide the asset name", type=str)
        else:
            value = token_name
    elif token_name is not None and token_name != value:
        raise click.BadParameter("Token name in metadata JSON must match CLI argument providing token name!")

    if value is None:
        raise click.BadParameter("Asset name cannot be None")

    if len(value.encode("utf-8")) <= MAX_ASSET_NAME_BYTE_LENGTH:
        return value
    else:
        raise click.BadParameter(
            f"Unit name must be {MAX_UNIT_NAME_BYTE_LENGTH} bytes or less.", ctx=context, param=param
        )

def _get_creator_account(_: click.Context, __: click.Parameter, value: str) -> SigningAccount:
    """
    Validate the creator account by checking if it is a valid Algorand address.

    Args:
        context (click.Context): The click context.
        value (str): The value of the parameter.

    Returns:
        SigningAccount: An account object with the address and private key.
    """
    try:
        return get_account_with_private_key(value)
    except Exception as ex:
        raise click.BadParameter(str(ex)) from ex

def is_algorand_address(value: str) -> bool:
    try:
        validate_address(value)
        return True
    except Exception:
        return False

def nfd_lookup(
    value: str,
    output: str,
) -> None:
    if not is_nfd(value) and not is_algorand_address(value):
        raise click.ClickException("Invalid input. Must be either a valid NFD domain or an Algorand address.")

    try:
        if is_nfd(value):
            click.echo(nfd_lookup_by_domain(value, NFDMatchType(output)))
        elif is_algorand_address(value):
            click.echo(nfd_lookup_by_address(value, NFDMatchType(output)))
    except Exception as err:
        raise click.ClickException(str(err)) from err

def _get_signed_transactions(file: Path | None = None, transaction: str | None = None) -> list[SignedTransaction]:
    """
    Retrieves a list of signed transactions.

    Args:
        file (Optional[Path]): A `Path` object representing the file path from which to retrieve the transactions.
        transaction (Optional[str]): A base64 encoded string representing a single signed transaction.

    Returns:
        list[SignedTransaction]: A list of `SignedTransaction` objects representing the retrieved signed transactions.

    Raises:
        click.ClickException: If the supplied transaction is not of type `SignedTransaction`.
        click.ClickException: If there is an error decoding the transaction.

    """
    try:
        if file:
            txns = retrieve_from_file(str(file))  # type: ignore[no-untyped-call]
        elif transaction:
            txns = [encoding.msgpack_decode(transaction)]  # type: ignore[no-untyped-call]
        else:
            txns = _load_from_stdin()

        for txn in txns:
            if not isinstance(txn, SignedTransaction):
                raise click.ClickException("Supplied transaction is not signed!")

        return cast(list[SignedTransaction], txns)

    except Exception as ex:
        logger.debug(ex, exc_info=True)
        raise click.ClickException(
            "Failed to decode transaction! If you are intending to send multiple transactions use `--file` instead."
        ) from ex

def send(*, file: Path | None, transaction: str | None, network: AlgorandNetwork) -> None:
    if not file and not transaction and not stdin_has_content():
        raise click.ClickException(
            "Please provide a file path via `--file` or a base64 encoded signed transaction via `--transaction`. "
            "Alternatively, you can also pipe the output of `algokit task sign` to this command."
        )

    txns = _get_signed_transactions(file, transaction)

    if not txns:
        raise click.ClickException("No valid transactions found!")

    try:
        _send_transactions(network, txns)
    except error.AlgodHTTPError as ex:
        raise click.ClickException(str(ex)) from ex
    except Exception as ex:
        logger.debug(ex, exc_info=True)
        raise click.ClickException("Failed to send transaction!") from ex

class TransactionBytesEncoder(json.JSONEncoder):
    def default(self, obj: Any) -> Any:  # noqa: ANN401
        if isinstance(obj, bytes | bytearray):
            return base64.b64encode(obj).decode()
        return super().default(obj)

def _validate_for_signed_txns(txns: list[Transaction]) -> None:
    signed_txns = [txn for txn in txns if isinstance(txn, SignedTransaction)]

    if signed_txns:
        transaction_ids = ", ".join([txn.get_txid() for txn in signed_txns])  # type: ignore[no-untyped-call]
        message = f"Supplied transactions {transaction_ids} are already signed!"
        raise click.ClickException(message)

def _get_transactions(file: Path | None, transaction: str | None) -> list[Transaction]:
    try:
        if file:
            txns: list[Transaction] = retrieve_from_file(str(file))  # type: ignore[no-untyped-call]
            return txns
        else:
            return [cast(Transaction, encoding.msgpack_decode(transaction))]  # type: ignore[no-untyped-call]
    except Exception as ex:
        logger.debug(ex, exc_info=True)
        raise click.ClickException(
            "Failed to decode transaction! If you are intending to sign multiple transactions use `--file` instead."
        ) from ex

def _confirm_transaction(txns: list[Transaction]) -> bool:
    click.echo(
        json.dumps(
            [
                {
                    "transaction_id": txn.get_txid(),  # type: ignore[no-untyped-call]
                    "content": txn.dictify(),  # type: ignore[no-untyped-call]
                }
                for txn in txns
            ],
            cls=TransactionBytesEncoder,
            indent=2,
        ),
    )
    response = click.prompt(
        "Would you like to proceed with signing the above?", type=click.Choice(["y", "n"]), default="n"
    )
    return bool(response == "y")

def _sign_and_output_transaction(txns: list[Transaction], private_key: str, output: Path | None) -> None:
    signed_txns = [txn.sign(private_key) for txn in txns]  # type: ignore[no-untyped-call]

    if output:
        write_to_file(signed_txns, str(output))  # type: ignore[no-untyped-call]
        click.echo(f"Signed transaction written to {output}")
    else:
        encoded_signed_txns = [
            {"transaction_id": txn.get_txid(), "content": encoding.msgpack_encode(txn)}  # type: ignore[no-untyped-call]
            for txn in signed_txns
        ]
        click.echo(json.dumps(encoded_signed_txns, indent=2))

def sign(*, account: str, file: Path | None, transaction: str | None, output: Path | None, force: bool) -> None:
    if not file and not transaction:
        raise click.ClickException(
            "Please provide a file path via `--file` or a base64 encoded unsigned transaction via `--transaction`."
        )

    signer_account = get_account_with_private_key(account)

    txns = _get_transactions(file, transaction)

    if not txns:
        raise click.ClickException("No valid transactions found!")

    _validate_for_signed_txns(txns)

    if not force and not _confirm_transaction(txns):
        return

    _sign_and_output_transaction(txns, signer_account.private_key, output)

def _validate_asset_balance(account_info: dict, asset_id: int, decimals: int, amount: int = 0) -> None:
    asset_record = next((asset for asset in account_info.get("assets", []) if asset["asset-id"] == asset_id), None)

    if not asset_record:
        raise click.ClickException("SigningAccount is not opted into the asset")

    if amount > 0 and asset_record["amount"] < amount:
        required = amount / 10**decimals
        available = asset_record["amount"] / 10**decimals
        raise click.ClickException(
            f"Insufficient asset balance in account, required: {required}, available: {available}"
        )

def _validate_algo_balance(account_info: dict, amount: int) -> None:
    if account_info.get("amount", 0) < amount:
        required = AlgoAmount.from_micro_algo(amount)
        available = AlgoAmount.from_micro_algo(account_info.get("amount", 0))
        raise click.ClickException(
            f"Insufficient Algos balance in account, required: {required.algo} Algos, available: {available.algo} Algos"
        )

def load_algod_client(network: AlgorandNetwork) -> algosdk.v2client.algod.AlgodClient:
    """
    Returns an instance of the `algosdk.v2client.algod.AlgodClient` class for the specified network.

    Args:
        network (str): The network for which the `AlgodClient` instance needs to be loaded.

    Returns:
        algosdk.v2client.algod.AlgodClient: An instance of the `AlgodClient` class for the specified network.

    Raises:
        click.ClickException: If the specified network is invalid.
    """

    config_mapping = {
        AlgorandNetwork.LOCALNET: ClientManager.get_default_localnet_config("algod"),
        AlgorandNetwork.TESTNET: ClientManager.get_algonode_config("testnet", "algod"),
        AlgorandNetwork.MAINNET: ClientManager.get_algonode_config("mainnet", "algod"),
    }
    try:
        return ClientManager.get_algod_client(config_mapping[network])
    except KeyError as err:
        raise click.ClickException("Invalid network") from err

def get_account_with_private_key(address: str) -> SigningAccount:
    """
    Retrieves an account object with the private key based on the provided address.

    Args:
        address (str): The address for which to retrieve the account object.

    Returns:
        SigningAccount: An account object with the address and private key.

    Raises:
        click.ClickException: If the address is not valid or if the alias does not exist or does not have a private key.
    """

    parsed_address = address.strip('"')

    try:
        validate_address(parsed_address)
        pk = get_private_key_from_mnemonic()
        return SigningAccount(address=parsed_address, private_key=pk)
    except click.ClickException as ex:
        alias_data = get_alias(parsed_address)

        if not alias_data:
            raise click.ClickException(f"Alias `{parsed_address}` alias does not exist.") from ex
        if not alias_data.private_key:
            raise click.ClickException(f"Alias `{parsed_address}` does not have a private key.") from ex

        return SigningAccount(address=alias_data.address, private_key=alias_data.private_key)

def _validate_inputs(
    keyword: str,
    output: str,
    alias: str | None,
    output_file: Path | None,
) -> None:
    if not re.match("^[A-Z2-7]+$", keyword):
        raise click.ClickException("Invalid KEYWORD. Allowed: uppercase letters A-Z and numbers 2-7.")
    if output == "alias" and not alias:
        raise click.ClickException(
            "Please provide an alias using the '--alias' option when the output is set to 'alias'."
        )
    if output == "file" and not output_file:
        raise click.ClickException(
            "Please provide an output filename using the '--file-path' option when the output is set to 'file'."
        )

def _store_vanity_to_alias(*, alias: str, vanity_account: VanityAccount, force: bool) -> None:
    logger.info(f"Adding {vanity_account.address} to wallet alias named {alias}")
    if get_alias(alias) and not force:
        response = click.prompt(
            f"Alias '{alias}' already exists. Overwrite?",
            type=click.Choice(["y", "n"]),
            default="n",
        )
        if response == "n":
            return

    try:
        add_alias(alias, vanity_account.address, vanity_account.private_key)
    except WalletAliasingLimitError as ex:
        raise click.ClickException(f"Reached the max of {WALLET_ALIASING_MAX_LIMIT} aliases.") from ex
    except Exception as ex:
        raise click.ClickException("Failed to add alias") from ex
    else:
        click.echo(f"Alias '{alias}' added successfully.")

def _validate_alias_name(alias_name: str) -> None:
    pattern = r"^[\w-]{1,20}$"
    if not re.match(pattern, alias_name):
        raise click.ClickException(
            "Invalid alias name. It should have at most 20 characters consisting of numbers, "
            "letters, dashes, or underscores."
        )

def add(*, alias_name: str, address: str, use_mnemonic: bool, force: bool) -> None:
    """Add an address or account to be stored against a named alias (at most 50 aliases)."""

    _validate_alias_name(alias_name)
    validate_address(address)

    private_key = get_private_key_from_mnemonic() if use_mnemonic else None

    if use_mnemonic:
        derived_address = account.address_from_private_key(private_key)  # type: ignore[no-untyped-call]
        if derived_address != address:
            click.echo(
                "Warning: Address from the mnemonic doesn't match the provided address. "
                "It won't work unless the account has been rekeyed."
            )

    if get_alias(alias_name) and not force:
        response = click.prompt(
            f"Alias '{alias_name}' already exists. Overwrite?",
            type=click.Choice(["y", "n"]),
            default="n",
        )
        if response == "n":
            return

    try:
        add_alias(alias_name, address, private_key)
    except WalletAliasingLimitError as ex:
        raise click.ClickException(f"Reached the max of {WALLET_ALIASING_MAX_LIMIT} aliases.") from ex
    except Exception as ex:
        raise click.ClickException("Failed to add alias") from ex
    else:
        click.echo(f"Alias '{alias_name}' added successfully.")

def get(alias: str) -> None:
    """Get an address or account stored against a named alias."""
    alias_data = get_alias(alias)

    if not alias_data:
        raise click.ClickException(f"Alias `{alias}` does not exist.")

    click.echo(
        f"Address for alias `{alias}`: {alias_data.address}"
        f"{' (🔐 includes private key)' if alias_data.private_key else ''}"
    )

def remove(*, alias: str, force: bool) -> None:
    """Remove an address or account stored against a named alias."""

    alias_data = get_alias(alias)

    if not alias_data:
        raise click.ClickException(f"Alias `{alias}` does not exist.")

    if not force:
        response = click.prompt(
            f"🚨 This is a destructive action that will remove the `{alias_data.alias}` alias. Are you sure?",
            type=click.Choice(["y", "n"]),
            default="n",
        )

        if response == "n":
            return

    remove_alias(alias)

    click.echo(f"Alias `{alias}` removed successfully.")

def reset(*, force: bool) -> None:
    """Remove all aliases."""

    aliases = get_aliases()

    if not aliases:
        click.echo("Warning: No aliases available to reset.")
        return

    if not force:
        response = click.prompt(
            "🚨 This is a destructive action that will clear all aliases. Are you sure?",
            type=click.Choice(["y", "n"]),
            default="n",
        )

        if response == "n":
            return

    for alias_data in aliases:
        try:
            remove_alias(alias_data.alias)
        except Exception as ex:
            raise click.ClickException(f"Failed to remove alias {alias_data.alias}") from ex

    click.echo("All aliases have been cleared.")

def _find_next_available_port(start_port: int, ignore_ports: list[int]) -> int:
    port = start_port
    while _is_port_in_use(port) or port in ignore_ports:
        port += 1
    return port

def _ensure_command_available(command: list[str], error_message: str) -> None:
    """
    Ensures that the specified command is available on the system.
    """
    try:
        proc.run(command)
    except Exception as e:
        raise RuntimeError(error_message) from e

def ensure_github_cli_installed() -> None:
    """
    Ensures GitHub CLI (`gh`) is installed, installing it if necessary.
    """
    try:
        proc.run(["gh", "--version"])
    except Exception as err:
        logger.info("Installing gh...")
        try:
            install_github_cli_via_webi()
        except Exception as e:
            logger.error(f"Failed to automatically install gh cli: {e}")
            logger.error(
                "Please install `gh cli` manually by following official documentation at https://cli.github.com/"
            )
            raise
        logger.info("gh installed successfully!")
        logger.warning(
            "Restart your terminal to activate the `gh` CLI and re-run `algokit localnet codespace` to get started..."
        )
        raise click.exceptions.Exit(code=0) from err

def get_app_config_dir() -> Path:
    """Get the application config files location - things that should persist, and potentially follow a user"""
    os_type = platform.system().lower()
    if os_type == "windows":
        config_dir = os.getenv("APPDATA")
    else:
        config_dir = os.getenv("XDG_CONFIG_HOME")
    if config_dir is None:
        config_dir = "~/.config"
    return _get_relative_app_path(config_dir)

def get_app_state_dir() -> Path:
    """Get the application state files location - things the user wouldn't normally interact with directly"""
    os_type = platform.system().lower()
    if os_type == "windows":
        state_dir = os.getenv("LOCALAPPDATA")
    elif os_type == "darwin":
        state_dir = "~/Library/Application Support"
    else:
        state_dir = os.getenv("XDG_STATE_HOME")
    if state_dir is None:
        state_dir = "~/.local/state"
    return _get_relative_app_path(state_dir)

def _get_relative_app_path(base_dir: str) -> Path:
    path = Path(base_dir).expanduser()
    result = path / PACKAGE_NAME
    result.mkdir(parents=True, exist_ok=True)
    # resolve path in case of UWP sandbox redirection
    return result.resolve()

def get_algokit_config(*, project_dir: Path | None = None, verbose_validation: bool = False) -> dict[str, t.Any] | None:
    """
    Load and parse a TOML configuration file. Will never throw.
    :param project_dir: Project directory path.
    :param verbose_validation: Whether to warn user if toml validation failed.
    :return: A dictionary containing the configuration or None if not found.
    """
    project_dir = project_dir or Path.cwd()
    config_path = project_dir / ALGOKIT_CONFIG
    logger.debug(f"Attempting to load project config from {config_path}")
    try:
        config_text = config_path.read_text("utf-8")
    except FileNotFoundError:
        logger.debug(f"No {ALGOKIT_CONFIG} file found in the project directory.")
        return None
    except Exception as ex:
        logger.debug(f"Unexpected error reading {ALGOKIT_CONFIG} file: {ex}", exc_info=True)
        return None
    try:
        return tomllib.loads(config_text)
    except Exception as ex:
        if verbose_validation:
            logger.warning(f"{ALGOKIT_CONFIG} file at {project_dir} is not valid toml! Skipping...", exc_info=True)
        else:
            logger.debug(f"Error parsing {ALGOKIT_CONFIG} file: {ex}", exc_info=True)
        return None

class DispenserApiAudiences(str, Enum):
    USER = "user"
    CI = "ci"

class AuthConfig:
    DOMAIN = "dispenser-prod.eu.auth0.com"
    BASE_URL = f"https://{DOMAIN}"
    JWKS_URL = f"{BASE_URL}/.well-known/jwks.json"
    OAUTH_TOKEN_URL = f"{BASE_URL}/oauth/token"
    OAUTH_DEVICE_CODE_URL = f"{BASE_URL}/oauth/device/code"
    OAUTH_REVOKE_URL = f"{BASE_URL}/oauth/revoke"
    AUDIENCES: ClassVar[dict[str, str]] = {
        DispenserApiAudiences.USER: "api-prod-dispenser-user",
        DispenserApiAudiences.CI: "api-prod-dispenser-ci",
    }
    CLIENT_IDS: ClassVar[dict[str, str]] = {
        DispenserApiAudiences.USER: "UKcJQcqFaZRQvik45QW5lsSRERUf8Ub6",
        DispenserApiAudiences.CI: "BOZkxGUiiWkaAXZebCQ20MTIYuQSqqpI",
    }

def _get_auth_token() -> str:
    """
    Retrieve the authorization token based on the environment.
    CI environment variables take precedence over keyring.
    """
    try:
        ci_access_token = os.environ.get(DISPENSER_ACCESS_TOKEN_KEY)

        if ci_access_token:
            logger.debug("Using CI access token over keyring credentials")

        return ci_access_token if ci_access_token else _get_dispenser_credentials().access_token
    except Exception as ex:
        raise Exception("Token not found") from ex

def _get_access_token_rsa_pub_key(access_token: str) -> rsa.RSAPublicKey:
    """
    Fetch the RSA public key based on provided access token.
    """
    jwks = httpx.get(AuthConfig.JWKS_URL).json()
    for key in jwks["keys"]:
        if key["kid"] == jwt.get_unverified_header(access_token)["kid"]:
            return rsa.RSAPublicNumbers(
                e=int.from_bytes(base64.urlsafe_b64decode(key["e"] + "=="), byteorder="big"),
                n=int.from_bytes(base64.urlsafe_b64decode(key["n"] + "=="), byteorder="big"),
            ).public_key()

    raise Exception("No matching key found")

def _refresh_user_access_token() -> None:
    """
    Refresh the user access token.
    """

    data = _get_dispenser_credentials()
    headers = {
        "Content-Type": "application/x-www-form-urlencoded",
        "Authorization": f"Bearer {data.access_token}",
    }
    token_data = {
        "grant_type": "refresh_token",
        "client_id": AuthConfig.CLIENT_IDS[DispenserApiAudiences.USER],
        "refresh_token": data.refresh_token,
    }
    response = httpx.post(
        AuthConfig.OAUTH_TOKEN_URL, data=token_data, headers=headers, timeout=DISPENSER_REQUEST_TIMEOUT
    )
    response.raise_for_status()

    set_dispenser_credentials(response.json())

def _get_hours_until_reset(resets_at: str) -> float:
    now_utc = datetime.now(timezone.utc)
    reset_date = datetime.strptime(resets_at, "%Y-%m-%dT%H:%M:%S.%fZ").replace(tzinfo=timezone.utc)
    return round((reset_date - now_utc).total_seconds() / 3600, 1)

def process_dispenser_request(*, url_suffix: str, data: dict | None = None, method: str = "POST") -> httpx.Response:
    """
    Generalized method to process http requests to dispenser API
    """

    headers = {"Authorization": f"Bearer {_get_auth_token()}"}

    # Set request arguments
    request_args = {
        "url": f"{ApiConfig.BASE_URL}/{url_suffix}",
        "headers": headers,
        "timeout": DISPENSER_REQUEST_TIMEOUT,
    }

    if method.upper() != "GET" and data is not None:
        request_args["json"] = data

    try:
        response: httpx.Response = getattr(httpx, method.lower())(**request_args)
        response.raise_for_status()
        return response

    except httpx.HTTPStatusError as err:
        error_message = f"Error processing dispenser API request: {err.response.status_code}"
        error_response = None
        with contextlib.suppress(Exception):
            error_response = err.response.json()

        if error_response and error_response.get("code") == APIErrorCode.FUND_LIMIT_EXCEEDED:
            hours_until_reset = _get_hours_until_reset(error_response.get("resetsAt"))
            error_message = (
                "Limit exceeded. "
                f"Try again in ~{hours_until_reset} hours if your request doesn't exceed the daily limit."
            )

        elif err.response.status_code == httpx.codes.BAD_REQUEST:
            error_message = err.response.json()["message"]

        raise Exception(error_message) from err

    except Exception as err:
        error_message = "Error processing dispenser API request"
        logger.debug(f"{error_message}: {err}", exc_info=True)
        raise err

def revoke_refresh_token() -> None:
    """
    Revoke the refresh token.
    """

    data = _get_dispenser_credentials()

    if not data.refresh_token:
        logger.debug("No refresh token found, nothing to revoke.")
        return

    payload = {"token": data.refresh_token, "client_id": AuthConfig.CLIENT_IDS[DispenserApiAudiences.USER]}
    headers = {"content-type": "application/json"}

    try:
        response = httpx.post(AuthConfig.OAUTH_REVOKE_URL, json=payload, headers=headers)
        response.raise_for_status()
        logger.debug("Token revoked successfully")
    except httpx.HTTPStatusError as ex:
        raise Exception(f"Failed to revoke token: {ex}") from ex
    except Exception as ex:
        raise Exception(f"An unexpected error occurred: {ex}") from ex

def _run_command(
    cmd: list[str],
    *,
    missing_help: list[str] | None = None,
) -> DoctorResult:
    try:
        proc_result = proc.run(cmd)
    except FileNotFoundError:
        logger.debug("Command not found", exc_info=True)
        return DoctorResult(ok=False, output="Command not found!", extra_help=missing_help)
    except PermissionError:
        logger.debug("Permission denied running command", exc_info=True)
        return DoctorResult(ok=False, output="Permission denied attempting to run command")
    except Exception as ex:
        logger.debug(f"Unexpected exception running command: {ex}", exc_info=True)
        return DoctorResult(
            ok=False,
            output="Unexpected error running command",
            extra_help=_format_exception_only(ex),
        )
    else:
        if proc_result.exit_code != 0:
            return DoctorResult(
                ok=False,
                output=f"Command exited with code: {proc_result.exit_code}",
                extra_help=proc_result.output.splitlines(),
            )
        return DoctorResult(ok=True, output=proc_result.output)

def _process_version(
    *,
    run_output: str,
    minimum_version: str | None,
    minimum_version_help: list[str] | None,
) -> DoctorResult:
    try:
        version_output = _get_version_or_first_non_blank_line(run_output)
    except Exception as ex:
        logger.debug(f"Unexpected error checking dependency: {ex}", exc_info=True)
        return DoctorResult(
            ok=False,
            output="Unexpected error checking dependency",
            extra_help=_format_exception_only(ex),
        )
    if minimum_version is not None:
        try:
            version_triple = extract_version_triple(version_output)
            version_ok = is_minimum_version(version_triple, minimum_version)
        except Exception as ex:
            logger.debug(f"Unexpected error parsing version: {ex}", exc_info=True)
            return DoctorResult(
                ok=False,
                output=version_output,
                extra_help=[
                    f'Failed to parse version from: "{version_output}"',
                    f"Error: {ex}",
                    f"Unable to check against minimum version of {minimum_version}",
                ],
            )
        if not version_ok:
            return DoctorResult(
                ok=False,
                output=version_output,
                extra_help=(minimum_version_help or [f"Minimum version required: {minimum_version}"]),
            )
    return DoctorResult(ok=True, output=version_output)

def _get_version_or_first_non_blank_line(output: str) -> str:
    match = re.search(r"\d+\.\d+\.\d+[^\s'\"(),]*", output)
    if match:
        return match.group()
    lines = output.splitlines()
    non_blank_lines = filter(None, (ln.strip() for ln in lines))
    # return first non-blank line or empty string if all blank
    return next(non_blank_lines, "")

def get_volume_mount_path_local(directory_name: str) -> Path:
    path = get_app_config_dir().joinpath(directory_name, "goal_mount")
    if get_container_engine() == ContainerEngine.PODMAN:
        # Pre create the directory to avoid permission issues
        path.mkdir(parents=True, exist_ok=True)
    return path

def is_path_or_filename(argument: str) -> bool:
    path = PurePath(argument)
    return len(path.parts) > 1 or (len(path.parts) == 1 and filename_pattern.match(path.parts[0]) is not None)

def delete_files_from_volume_mount(filename: str, volume_mount_path_docker: Path) -> None:
    try:
        volume_mount_path_docker.joinpath(filename).unlink()
    except Exception as e:
        logger.error(e)

def list_files_in_volume(volume_path: Path) -> list[str]:
    file_paths = []
    if volume_path.exists() and volume_path.is_dir():
        for file in volume_path.rglob("*"):
            if file.is_file():
                file_paths.append(str(file))
    else:
        logger.error(f"{volume_path} does not exist or is not a directory.")
    return file_paths

def post_process(input_files: list[Path], output_files: list[Path], volume_mount_path_local: Path) -> None:
    for input_file in input_files:
        delete_files_from_volume_mount(input_file.name, volume_mount_path_local)

    files_in_volume_mount = {Path(file) for file in list_files_in_volume(volume_mount_path_local)}
    for output_file in output_files:
        stem = output_file.stem
        ext = output_file.suffix

        # Copy outputs split into multiple files. For example `goal clerk split -i ./input.gtxn -o ./output.txn`
        # will produce a file (output-0.txn etc) for each transaction in the group being split.
        r = re.compile(rf"^(?:{stem})(?:-[0-9]+)?(?:\{ext})$") if ext else re.compile(rf"^(?:{stem})(?:-[0-9]+)?$")

        matched_files_in_volume_mount = filter(lambda f: (r.match(f.name)), files_in_volume_mount)

        for matched_file_in_volume_mount in matched_files_in_volume_mount:
            shutil.copy(
                volume_mount_path_local.joinpath(matched_file_in_volume_mount.name),
                output_file.parent.joinpath(matched_file_in_volume_mount.name),
            )
            delete_files_from_volume_mount(matched_file_in_volume_mount.name, volume_mount_path_local)

def get_git_user_info(param: str) -> str | None:
    """Get git user info from the system. Returns None if git is not available."""

    from subprocess import check_output

    if not shutil.which("git"):
        return None

    try:
        return check_output(f"git config user.{param}", shell=True).decode("utf-8").strip()
    except Exception:
        logger.warning(
            f"Failed to get user info from git, please input your '{param}' manually or use default placeholder."
        )
        logger.debug("Failed to get user info from git", exc_info=True)
        return None

def is_valid_project_dir_name(value: str) -> bool:
    """Check if the project directory name for algokit project is valid."""

    algokit_project_names = get_project_dir_names_from_workspace()
    if value in algokit_project_names:
        return False
    if not re.match(r"^[\w\-.]+$", value):
        return False
    return True

def resolve_vscode_workspace_file(project_root: Path | None) -> Path | None:
    """Resolve the path to the VSCode workspace file for the given project.
    Works by looking for algokit workspace and checking if there is a matching
    vscode config at the same level."""
    if not project_root:
        return None
    return next(project_root.glob("*.code-workspace"), None)

def append_project_to_vscode_workspace(project_path: Path, workspace_path: Path) -> None:
    """Append project to the code workspace, ensuring compatibility across Windows and Unix systems."""
    if not workspace_path.exists():
        raise FileNotFoundError(f"Workspace path {workspace_path} does not exist.")

    try:
        workspace = _load_vscode_workspace(workspace_path)

        # Compute the project path relative to the workspace root
        processed_project_path = project_path.relative_to(workspace_path.parent)
        project_abs_path = (workspace_path.parent / processed_project_path).resolve(strict=False)

        # Gather existing paths as absolute paths
        existing_abs_paths = []
        for folder in workspace.get("folders", []):
            folder_path = Path(folder.get("path", "").replace("\\", "/"))
            existing_abs_path = (workspace_path.parent / folder_path).resolve(strict=False)
            existing_abs_paths.append(existing_abs_path)

        # Check if the project path is already in the workspace
        if project_abs_path not in existing_abs_paths:
            workspace.setdefault("folders", []).append({"path": str(processed_project_path).replace("\\", "/")})
            _save_vscode_workspace(workspace_path, workspace)
            logger.debug(f"Appended project {project_path} to workspace {workspace_path}.")
        else:
            logger.debug(f"Project {project_path} is already in workspace {workspace_path}, not appending.")

    except json.JSONDecodeError as json_err:
        logger.warning(f"Invalid JSON format in the workspace file {workspace_path}. {json_err}")
    except Exception as e:
        logger.warning(f"Failed to append project {project_path} to workspace {workspace_path}. {e}")

def _load_vscode_workspace(workspace_path: Path) -> dict[str, Any]:
    """Load the workspace file as a JSON object."""
    with workspace_path.open(mode="r", encoding="utf-8") as f:
        data = json.load(f)
        assert isinstance(data, dict)
        return cast(dict[str, Any], data)

class ClickHandler(logging.Handler):
    """Handle console output with click.echo(...)

    Slightly special in that this class acts as both a sink and an additional formatter,
    but they're kind of intertwined for our use case of actually displaying things to the user.
    """

    styles: ClassVar[dict[str, dict[str, Any]]] = {
        "critical": {"fg": "red", "bold": True},
        "error": {"fg": "red"},
        "warning": {"fg": "yellow"},
        "debug": {"fg": "cyan"},
    }

    def emit(self, record: logging.LogRecord) -> None:
        try:
            msg = self.format(record)
            level = record.levelname.lower()
            if level in self.styles:
                # if user hasn't disabled colors/styling, just use that
                if resolve_color_default() is not False:
                    level_style = self.styles[level]
                    msg = click.style(msg, **level_style)
                # otherwise, prefix the level name
                else:
                    msg = f"{level.upper()}: {msg}"
            click.echo(msg)
        except Exception:
            self.handleError(record)

class ManualExclusionFilter(logging.Filter):
    def __init__(self, exclude_value: str):
        super().__init__()
        self.exclude_value = exclude_value

    def filter(self, record: logging.LogRecord) -> bool:
        return getattr(record, EXCLUDE_FROM_KEY, None) != self.exclude_value

def uncaught_exception_logging_handler(
    exc_type: type[BaseException], exc_value: BaseException, exc_traceback: TracebackType | None
) -> None:
    """Function to be used as sys.excepthook, which logs uncaught exceptions."""
    if issubclass(exc_type, KeyboardInterrupt):
        # don't log ctrl-c or equivalents
        sys.__excepthook__(exc_type, exc_value, exc_traceback)
    else:
        logging.critical(f"Unhandled {exc_type.__name__}: {exc_value}", exc_info=(exc_type, exc_value, exc_traceback))

def _set_verbose(_ctx: click.Context, _param: click.Option, value: bool) -> None:  # noqa: FBT001
    if value:
        for handler in logging.getLogger().handlers:
            if handler.name == CONSOLE_LOG_HANDLER_NAME:
                handler.setLevel(logging.DEBUG)
                return
        raise RuntimeError(f"Couldn't locate required logger named {CONSOLE_LOG_HANDLER_NAME}")

def _set_force_styles_to(ctx: click.Context, _param: click.Option, value: bool | None) -> None:
    if value is not None:
        ctx.color = value

class NonEmptyValidator(questionary.Validator):
    def validate(self, document: prompt_toolkit.document.Document) -> None:
        value = document.text.strip()
        if not value:
            raise questionary.ValidationError(message="Please enter a value")

class ChainedValidator(questionary.Validator):
    def __init__(self, *validators: questionary.Validator):
        self._validators = validators

    def validate(self, document: prompt_toolkit.document.Document) -> None:
        for validator in self._validators:
            validator.validate(document)

def prompt_text(
    message: str,
    *,
    validators: Sequence[type[questionary.Validator] | questionary.Validator | Callable[[str], bool]] | None = None,
    validate_while_typing: bool = False,
) -> str:
    if validators:
        validate, *others = filter(None, map(build_validator, validators))
        if others:
            validate = ChainedValidator(validate, *others)
    else:
        validate = None
    result = questionary.text(
        message,
        validate=validate,
        validate_while_typing=validate_while_typing,
    ).unsafe_ask()
    assert isinstance(result, str)
    return result

def prompt_select(
    message: str,
    *choices: str | questionary.Choice,
) -> Any:  # noqa: ANN401
    return questionary.select(
        message,
        choices=choices,
    ).unsafe_ask()

class ContainerEngine(str, enum.Enum):
    DOCKER = "docker"
    PODMAN = "podman"

    def __str__(self) -> str:
        return self.value

def get_min_compose_version() -> str:
    container_engine = get_container_engine()
    return (
        DOCKER_COMPOSE_MINIMUM_VERSION if container_engine == ContainerEngine.DOCKER else PODMAN_COMPOSE_MINIMUM_VERSION
    )

class ComposeSandbox:
    def __init__(self, name: str = SANDBOX_BASE_NAME, config_path: Path | None = None) -> None:
        self.name = SANDBOX_BASE_NAME if name == SANDBOX_BASE_NAME else f"{SANDBOX_BASE_NAME}_{name}"
        self.directory = (config_path or get_app_config_dir()) / self.name
        if not self.directory.exists():
            logger.debug(f"The {self.name} directory does not exist yet; creating it")
            self.directory.mkdir()
        self._conduit_yaml = get_conduit_yaml()
        self._latest_yaml = get_docker_compose_yml(name=f"algokit_{self.name}")
        self._latest_config_json = get_config_json()
        self._latest_algod_network_template = get_algod_network_template()
        self._latest_proxy_config = get_proxy_config()

    @property
    def compose_file_path(self) -> Path:
        return self.directory / "docker-compose.yml"

    @property
    def conduit_file_path(self) -> Path:
        return self.directory / "conduit.yml"

    @property
    def algod_config_file_path(self) -> Path:
        return self.directory / "algod_config.json"

    @property
    def algod_network_template_file_path(self) -> Path:
        return self.directory / "algod_network_template.json"

    @property
    def proxy_config_file_path(self) -> Path:
        return self.directory / "nginx.conf"

    @classmethod
    def from_environment(cls) -> ComposeSandbox | None:
        try:
            run_results = run(
                [get_container_engine(), "compose", "ls", "--format", "json", "--filter", "name=algokit_sandbox*"],
                bad_return_code_error_message="Failed to list running LocalNet",
            )
            if run_results.exit_code != 0:
                return None
        except Exception as err:
            logger.debug(f"Error checking for existing sandbox: {err}", exc_info=True)
            return None

        try:
            json_lines = cls._extract_json_lines(run_results.output)
            if not json_lines:
                return None

            data = json.loads(json_lines[0])
            return cls._create_instance_from_data(data)
        except (json.JSONDecodeError, KeyError, IndexError) as err:
            logger.info(f"Error checking config file: {err}", exc_info=True)
            return None

    @staticmethod
    def _extract_json_lines(output: str) -> list[str]:
        valid_json_lines = []
        for line in output.splitlines():
            # strip ANSI color codes
            parsed_line = re.sub(r"\x1b\[[0-9;]*[a-zA-Z]", "", line)
            try:
                json.loads(parsed_line)
                valid_json_lines.append(parsed_line)
            except json.JSONDecodeError:
                continue
        return valid_json_lines

    @classmethod
    def _create_instance_from_data(cls, data: list[dict[str, Any]]) -> ComposeSandbox | None:
        for item in data:
            config_file = item.get("ConfigFiles", "").split(",")[0]
            config_file_path = Path(config_file)
            full_name = config_file_path.parent.name
            name = (
                full_name.replace(f"{SANDBOX_BASE_NAME}_", "")
                if full_name.startswith(f"{SANDBOX_BASE_NAME}_")
                else full_name
            )
            config_path = config_file_path.parent.parent
            return cls(name, config_path)
        return None

    def set_algod_dev_mode(self, *, dev_mode: bool) -> None:
        content = self.algod_network_template_file_path.read_text()
        new_value = "true" if dev_mode else "false"
        new_content = re.sub(r'"DevMode":\s*(true|false)', f'"DevMode": {new_value}', content)
        self.algod_network_template_file_path.write_text(new_content)

    def is_algod_dev_mode(self) -> bool:
        content = self.algod_network_template_file_path.read_text()
        search = re.search(r'"DevMode":\s*(true|false)', content)
        return search is not None and search.group(1) == "true"

    def compose_file_status(self) -> ComposeFileStatus:
        try:
            compose_content = self.compose_file_path.read_text()
            config_content = self.algod_config_file_path.read_text()
            algod_network_template_content = self.algod_network_template_file_path.read_text()
            proxy_config_content = self.proxy_config_file_path.read_text()

        except FileNotFoundError:
            # treat as out of date if compose file exists but algod config doesn't
            # so that existing setups aren't suddenly reset
            if self.compose_file_path.exists():
                return ComposeFileStatus.OUT_OF_DATE
            return ComposeFileStatus.MISSING
        else:
            algod_network_json_content = json.loads(
                algod_network_template_content.replace("NUM_ROUNDS", '"NUM_ROUNDS"')
            )
            latest_algod_network_json_content = json.loads(
                self._latest_algod_network_template.replace("NUM_ROUNDS", '"NUM_ROUNDS"')
            )

            del algod_network_json_content["Genesis"]["DevMode"]
            del latest_algod_network_json_content["Genesis"]["DevMode"]

            if (
                compose_content == self._latest_yaml
                and config_content == self._latest_config_json
                and algod_network_json_content == latest_algod_network_json_content
                and proxy_config_content == self._latest_proxy_config
            ):
                return ComposeFileStatus.UP_TO_DATE
            else:
                return ComposeFileStatus.OUT_OF_DATE

    def write_compose_file(self) -> None:
        self.conduit_file_path.write_text(self._conduit_yaml)
        self.compose_file_path.write_text(self._latest_yaml)
        self.algod_config_file_path.write_text(self._latest_config_json)
        self.algod_network_template_file_path.write_text(self._latest_algod_network_template)
        self.proxy_config_file_path.write_text(self._latest_proxy_config)

    def _run_compose_command(
        self,
        compose_args: str,
        stdout_log_level: int = logging.INFO,
        bad_return_code_error_message: str | None = None,
    ) -> RunResult:
        return run(
            [get_container_engine(), "compose", *compose_args.split()],
            cwd=self.directory,
            stdout_log_level=stdout_log_level,
            bad_return_code_error_message=bad_return_code_error_message,
        )

    def up(self) -> None:
        logger.info("Starting AlgoKit LocalNet now...")
        self._run_compose_command(
            f"up --detach --quiet-pull{' --wait' if get_container_engine() == ContainerEngine.DOCKER else ''}",
            bad_return_code_error_message="Failed to start LocalNet",
        )
        logger.debug("AlgoKit LocalNet started, waiting for health check")
        if _wait_for_algod():
            logger.info("Started; execute `algokit explore` to explore LocalNet in a web user interface.")
        else:
            logger.warning("AlgoKit LocalNet failed to return a successful health check")

    def stop(self) -> None:
        logger.info("Stopping AlgoKit LocalNet now...")
        self._run_compose_command("stop", bad_return_code_error_message="Failed to stop LocalNet")
        logger.info("LocalNet Stopped; execute `algokit localnet start` to start it again.")

    def down(self) -> None:
        logger.info("Cleaning up the running AlgoKit LocalNet...")
        self._run_compose_command("down", stdout_log_level=logging.DEBUG)

    def pull(self) -> None:
        logger.info("Fetching any container updates from DockerHub...")
        self._run_compose_command("pull --ignore-pull-failures --quiet")

    def logs(self, *, follow: bool = False, no_color: bool = False, tail: str | None = None) -> None:
        compose_args = ["logs"]
        if follow:
            compose_args += ["--follow"]
        if no_color:
            compose_args += ["--no-color"]
        if tail is not None:
            compose_args += ["--tail", tail]
        run_interactive(
            [get_container_engine(), "compose", *compose_args],
            cwd=self.directory,
            bad_return_code_error_message="Failed to get logs, are the containers running?",
        )

    def ps(self, service_name: str | None = None) -> list[dict[str, Any]]:
        run_results = self._run_compose_command(
            f"ps {service_name or ''} --format json", stdout_log_level=logging.DEBUG
        )
        if run_results.exit_code != 0:
            return []

        # `docker compose ps --format json` on version < 2.21.0 outputs a JSON arary
        if run_results.output.startswith("["):
            data = json.loads(run_results.output)
        # `docker compose ps --format json` on version >= 2.21.0 outputs seperate JSON objects, each on a new line
        else:
            json_lines = self._extract_json_lines(run_results.output)
            data = [json.loads(line) for line in json_lines]

        assert isinstance(data, list)
        return cast(list[dict[str, Any]], data)

    def _get_local_image_versions(self, image_name: str) -> list[str]:
        """
        Get the local versions of a Docker image. Note that a single image may be pulled from multiple repo digests.
        """
        try:
            arg = "{{range .RepoDigests}}{{println .}}{{end}}"
            local_versions_output = run([get_container_engine(), "image", "inspect", image_name, "--format", arg])
            return [line.split("@")[1] if "@" in line else line for line in local_versions_output.output.splitlines()]
        except Exception as e:
            logger.debug(f"Failed to get local image versions: {e}", exc_info=True)
            return []

    def _get_latest_image_version(self, image_name: str) -> str | None:
        """
        Get the latest version of a Docker image from Docker Hub
        """
        args = image_name.split(":")
        name = args[0]
        tag = args[1] if len(args) > 1 else "latest"
        url = f"https://registry.hub.docker.com/v2/repositories/{name}/tags/{tag}"
        try:
            data = httpx.get(url=url)
            return str(data.json()["digest"])
        except Exception as err:
            logger.debug(f"Error checking image status: {err}", exc_info=True)
            return None

    def is_image_up_to_date(self, image_name: str) -> bool:
        local_versions = self._get_local_image_versions(image_name)
        latest_version = self._get_latest_image_version(image_name)
        return latest_version is None or latest_version in local_versions

    def check_docker_compose_for_new_image_versions(self) -> None:
        is_indexer_up_to_date = self.is_image_up_to_date(INDEXER_IMAGE)
        if is_indexer_up_to_date is False:
            logger.warning(
                "indexer has a new version available, run `algokit localnet reset --update` to get the latest version"
            )

        is_algorand_up_to_date = self.is_image_up_to_date(ALGORAND_IMAGE)
        if is_algorand_up_to_date is False:
            logger.warning(
                "algod has a new version available, run `algokit localnet reset --update` to get the latest version"
            )

def _wait_for_algod() -> bool:
    end_time = time.time() + DEFAULT_WAIT_FOR_ALGOD
    last_exception: httpx.RequestError | None = None
    while time.time() < end_time:
        try:
            health = httpx.get(
                ALGOD_HEALTH_URL, timeout=DEFAULT_HEALTH_TIMEOUT, headers={"X-Algo-API-Token": DEFAULT_ALGOD_TOKEN}
            )
        except httpx.RequestError as ex:
            last_exception = ex
        else:
            if health.is_success:
                logger.debug("AlgoKit LocalNet health check successful, algod is ready")
                return True
            logger.debug(f"AlgoKit LocalNet health check returned {health.status_code}, waiting")
        time.sleep(DEFAULT_HEALTH_TIMEOUT)
    if last_exception:
        logger.debug("AlgoKit LocalNet health request failed", exc_info=last_exception)
    return False

