def fetch_algod_status_data(service_info: dict[str, Any]) -> dict[str, Any]:
    results: dict[str, Any] = {}
    try:
        # Docker image response
        # Search for DEFAULT_ALGOD_PORT in ports, if found use it, if not found this is an error
        if not any(item["PublishedPort"] == DEFAULT_ALGOD_PORT for item in service_info["Publishers"]):
            return {"Status": "Error"}

        results["Port"] = DEFAULT_ALGOD_PORT
        # container specific response
        with httpx.Client() as client:
            algod_headers = {"X-Algo-API-Token": DEFAULT_ALGOD_TOKEN}
            http_status_response = client.get(
                f"{DEFAULT_ALGOD_SERVER}:{DEFAULT_ALGOD_PORT}/v2/status", headers=algod_headers, timeout=3
            )
            http_versions_response = client.get(
                f"{DEFAULT_ALGOD_SERVER}:{DEFAULT_ALGOD_PORT}/versions", headers=algod_headers, timeout=3
            )
            if (
                http_status_response.status_code != httpx.codes.OK
                or http_versions_response.status_code != httpx.codes.OK
            ):
                return {"Status": "Error"}

            # status response
            status_response = http_status_response.json()
            results["Last round"] = status_response["last-round"]
            results["Time since last round"] = "%.1fs" % (status_response["time-since-last-round"] / 1e9)
            # genesis response
            genesis_response = http_versions_response.json()
            results["Genesis ID"] = genesis_response["genesis_id"]
            results["Genesis hash"] = genesis_response["genesis_hash_b64"]
            major_version = genesis_response["build"]["major"]
            minor_version = genesis_response["build"]["minor"]
            build_version = genesis_response["build"]["build_number"]
            results["Version"] = f"{major_version}.{minor_version}.{build_version}"
        return results
    except Exception as err:
        logger.debug(f"Error checking algod status: {err}", exc_info=True)
        return {"Status": "Error"}

def fetch_indexer_status_data(service_info: dict[str, Any]) -> dict[str, Any]:
    results: dict[str, Any] = {}
    try:
        # Docker image response
        if not any(item["PublishedPort"] == DEFAULT_INDEXER_PORT for item in service_info["Publishers"]):
            return {"Status": "Error"}

        results["Port"] = DEFAULT_INDEXER_PORT
        # container specific response
        health_url = f"{DEFAULT_ALGOD_SERVER}:{DEFAULT_INDEXER_PORT}/health"
        http_response = httpx.get(health_url, timeout=5)

        if http_response.status_code != httpx.codes.OK:
            return {"Status": "Error"}

        response = http_response.json()
        logger.debug(f"{health_url} response: {response}")
        results["Last round"] = response["round"]
        results["Version"] = response["version"]
        return results
    except Exception as err:
        logger.debug(f"Error checking indexer status: {err}", exc_info=True)
        return {"Status": "Error"}

def _snake_case(s: str) -> str:
    s = s.replace("-", " ")
    s = re.sub(r"([A-Z]+)([A-Z][a-z])", r"\1_\2", s)
    s = re.sub(r"([a-z\d])([A-Z])", r"\1_\2", s)
    return re.sub(r"[-\s]", "_", s).lower()

class AppSpecType(enum.Enum):
    ARC32 = "arc32"
    ARC56 = "arc56"

class ClientGenerator(abc.ABC):
    language: ClassVar[str]
    extension: ClassVar[str]
    version: str | None

    _by_language: ClassVar[dict[str, type["ClientGenerator"]]] = {}
    _by_extension: ClassVar[dict[str, type["ClientGenerator"]]] = {}

    def __init__(self, version: str | None) -> None:
        self.command = self.find_generate_command(version)

    def __init_subclass__(cls, language: str, extension: str) -> None:
        cls.language = language
        cls.extension = extension
        cls._by_language[language] = cls
        cls._by_extension[extension] = cls

    @classmethod
    def languages(cls) -> list[str]:
        return list(cls._by_language.keys())

    @classmethod
    def create_for_language(cls, language: str, version: str | None) -> "ClientGenerator":
        return cls._by_language[language](version)

    @classmethod
    def create_for_extension(cls, extension: str, version: str | None) -> "ClientGenerator":
        return cls._by_extension[extension](version)

    def resolve_output_path(self, app_spec: Path, output_path_pattern: str | None) -> tuple[Path, AppSpecType] | None:
        try:
            application_json = json.loads(app_spec.read_text())
            try:
                contract_name: str = application_json["name"]  # ARC-56
                app_spec_type: AppSpecType = AppSpecType.ARC56
            except KeyError:
                contract_name = application_json["contract"]["name"]  # ARC-32
                app_spec_type = AppSpecType.ARC32
        except Exception:
            logger.error(f"Couldn't parse contract name from {app_spec}", exc_info=True)
            return None
        output_resolved = (output_path_pattern or self.default_output_pattern).format(
            contract_name=self.format_contract_name(contract_name),
            app_spec_dir=str(app_spec.parent),
        )
        output_path = Path(output_resolved)
        if output_path.exists() and not output_path.is_file():
            logger.error(f"Could not output to {output_path} as it already exists and is a directory")
            return None
        return (output_path, app_spec_type)

    @abc.abstractmethod
    def generate(self, app_spec: Path, output: Path) -> None: ...

    @property
    @abc.abstractmethod
    def default_output_pattern(self) -> str: ...

    @abc.abstractmethod
    def find_generate_command(self, version: str | None) -> list[str]: ...

    def format_contract_name(self, contract_name: str) -> str:
        return contract_name

    def generate_all(
        self,
        app_spec_path_or_dir: Path,
        output_path_pattern: str | None,
        *,
        raise_on_path_resolution_failure: bool,
    ) -> None:
        if not app_spec_path_or_dir.is_dir():
            app_specs = [app_spec_path_or_dir]
        else:
            file_patterns = ["application.json", "*.arc32.json", "*.arc56.json"]
            app_specs = list(set(chain.from_iterable(app_spec_path_or_dir.rglob(pattern) for pattern in file_patterns)))
            app_specs.sort()
            if not app_specs:
                raise AppSpecsNotFoundError

        def accumulate_items_to_generate(
            acc: dict[Path, tuple[Path, AppSpecType]], app_spec: Path
        ) -> dict[Path, tuple[Path, AppSpecType]]:
            output_path_result = self.resolve_output_path(app_spec, output_path_pattern)
            if output_path_result is None:
                if raise_on_path_resolution_failure:
                    raise click.ClickException(f"Error generating client for {app_spec}")
                return acc
            (output_path, app_spec_type) = output_path_result
            if output_path in acc:
                # ARC-56 app specs take precedence over ARC-32 app specs
                if acc[output_path][1] == AppSpecType.ARC32 and app_spec_type == AppSpecType.ARC56:
                    acc[output_path] = (app_spec, app_spec_type)
            else:
                acc[output_path] = (app_spec, app_spec_type)
            return acc

        items_to_generate: dict[Path, tuple[Path, AppSpecType]] = reduce(accumulate_items_to_generate, app_specs, {})
        for output_path, (app_spec, _) in items_to_generate.items():
            self.generate(app_spec, output_path)

class PythonClientGenerator(ClientGenerator, language="python", extension=".py"):
    def generate(self, app_spec: Path, output: Path) -> None:
        logger.info(f"Generating Python client code for application specified in {app_spec} and writing to {output}")
        cmd = [
            *self.command,
            "-a",
            str(app_spec),
            "-o",
            str(output),
        ]
        run_result = proc.run(cmd)
        click.echo(run_result.output)

        if run_result.exit_code != 0:
            click.secho(
                f"Client generation failed for {app_spec}.",
                err=True,
                fg="red",
            )
            raise click.exceptions.Exit(run_result.exit_code)

    @property
    def default_output_pattern(self) -> str:
        return f"{{contract_name}}_client{self.extension}"

    def format_contract_name(self, contract_name: str) -> str:
        return _snake_case(contract_name)

    def find_project_generate_command(self, version: str | None) -> list[str] | None:
        """
        Try find the generate command in the project.
        """
        try:
            # Use the tree output as it puts the package info on the first line of the output
            result = proc.run(["poetry", "show", PYTHON_PYPI_PACKAGE, "--tree"])
            if result.exit_code == 0:
                generate_command = ["poetry", "run", PYTHON_GENERATE_COMMAND]
                if version is not None:
                    installed_version = None
                    lines = result.output.splitlines()
                    if len(lines) > 0:
                        installed_version = extract_version_triple(lines[0])
                        if extract_version_triple(version) == installed_version:
                            return generate_command
                else:
                    return generate_command
        except OSError:
            pass
        except ValueError:
            pass

        return None

    def find_global_generate_command(self, pipx_command: list[str], version: str | None) -> list[str] | None:
        """
        Try find the generate command installed globally.
        """
        try:
            result = proc.run([*pipx_command, "list", "--short"])
            if result.exit_code == 0:
                generate_command = [PYTHON_GENERATE_COMMAND]
                for line in result.output.splitlines():
                    if PYTHON_PYPI_PACKAGE in line:
                        if version is not None:
                            installed_version = None
                            installed_version = extract_version_triple(line)
                            if extract_version_triple(version) == installed_version:
                                return generate_command
                        else:
                            return generate_command
        except OSError:
            pass
        except ValueError:
            pass

        return None

    def find_generate_command(self, version: str | None) -> list[str]:
        """
        Find Python generator command.
        If a matching version is installed at a project level, use that.
        If a matching version is installed at a global level, use that.
        Otherwise, run the matching version via pipx.
        """

        logger.debug("Searching for project installed client generator")
        project_result = self.find_project_generate_command(version)
        if project_result is not None:
            return project_result

        pipx_command = find_valid_pipx_command(
            f"Unable to find pipx install so that the `{PYTHON_PYPI_PACKAGE}` can be run; "
            "please install pipx via https://pypa.github.io/pipx/ "
            "and then try `algokit generate client ...` again."
        )

        logger.debug("Searching for globally installed client generator")
        global_result = self.find_global_generate_command(pipx_command, version)
        if global_result is not None:
            return global_result

        # when not installed, run via pipx
        logger.debug("No matching installed client generator found, run client generator via pipx")
        return [
            *pipx_command,
            "run",
            f"--spec={PYTHON_PYPI_PACKAGE}{f'=={version}' if version is not None else ''}",
            PYTHON_GENERATE_COMMAND,
        ]

class TypeScriptClientGenerator(ClientGenerator, language="typescript", extension=".ts"):
    def generate(self, app_spec: Path, output: Path) -> None:
        cmd = [*self.command, "generate", "-a", str(app_spec), "-o", str(output)]
        logger.info(
            f"Generating TypeScript client code for application specified in {app_spec} and writing to {output}"
        )
        run_result = proc.run(cmd)
        click.echo(run_result.output)

        if run_result.exit_code != 0:
            click.secho(
                f"Client generation failed for {app_spec}.",
                err=True,
                fg="red",
            )
            raise click.exceptions.Exit(run_result.exit_code)

    def find_project_generate_command(
        self, npm_command: list[str], npx_command: list[str], version: str | None
    ) -> list[str] | None:
        try:
            result = proc.run([*npm_command, "ls", "--no-unicode"])
            # Normally we would check the exit code, however `npm ls` may return a non zero exit code
            # when certain dependencies are not met. We still want to continue processing.
            if result.output != "":
                generate_command = [*npx_command, TYPESCRIPT_NPM_PACKAGE]
                for line in result.output.splitlines():
                    if TYPESCRIPT_NPM_PACKAGE in line:
                        if "UNMET DEPENDENCY" in line:
                            raise ModuleNotFoundError(
                                f"{TYPESCRIPT_NPM_PACKAGE} was detected in the project, but is not installed."
                            )
                        if version is not None:
                            installed_version = extract_semantic_version(line)
                            if extract_semantic_version(version) == installed_version:
                                return generate_command
                        else:
                            return generate_command
        except OSError:
            pass
        except ValueError:
            pass

        return None

    def find_global_generate_command(
        self, npm_command: list[str], npx_command: list[str], version: str | None
    ) -> list[str] | None:
        return self.find_project_generate_command([*npm_command, "--global"], npx_command, version)

    def find_generate_command(self, version: str | None) -> list[str]:
        """
        Find TypeScript generator command.
        If a matching version is installed at a project level, use that.
        If a matching version is installed at a global level, use that.
        Otherwise, run the matching version via npx.
        """

        npm_command = get_npm_command(
            f"Unable to find npm install so that the `{TYPESCRIPT_NPM_PACKAGE}` can be run; "
            "please install npm via https://docs.npmjs.com/downloading-and-installing-node-js-and-npm "
            "and then try `algokit generate client ...` again.",
        )
        npx_command = get_npm_command(
            f"Unable to find npx install so that the `{TYPESCRIPT_NPM_PACKAGE}` can be run; "
            "please install npx via https://www.npmjs.com/package/npx "
            "and then try `algokit generate client ...` again.",
            is_npx=True,
        )

        logger.debug("Searching for project installed client generator")
        project_result = self.find_project_generate_command(npm_command, npx_command, version)
        if project_result is not None:
            return project_result

        logger.debug("Searching for globally installed client generator")
        global_result = self.find_global_generate_command(npm_command, npx_command, version)
        if global_result is not None:
            return global_result

        # when not installed, run via npx
        logger.debug("No matching installed client generator found, run client generator via npx")
        return [
            *npx_command,
            "--yes",
            f"{TYPESCRIPT_NPM_PACKAGE}@{version if version is not None else 'latest'}",
        ]

    @property
    def default_output_pattern(self) -> str:
        return f"{{contract_name}}Client{self.extension}"

def extract_version_triple(version_str: str) -> str:
    match = re.search(r"\d+\.\d+\.\d+", version_str)
    if not match:
        raise ValueError("Unable to parse version number")
    return match.group()

def extract_semantic_version(version_str: str) -> str:
    match = re.search(r"\d+\.\d+\.\d+(?:-[a-zA-Z0-9.]+)?", version_str)
    if not match:
        raise ValueError("Unable to parse version number")
    return match.group()

def is_network_available(host: str = "8.8.8.8", port: int = 53, timeout: float = 3.0) -> bool:
    """
    Check if internet is available by trying to establish a socket connection.
    """

    try:
        socket.setdefaulttimeout(timeout)
        with socket.create_connection((host, port), timeout=timeout):
            return True
    except OSError:
        return False

def find_valid_pipx_command(error_message: str) -> list[str]:
    for pipx_command in get_candidate_pipx_commands():
        try:
            pipx_version_result = proc.run([*pipx_command, "--version"])
        except OSError:
            pass  # in case of path/permission issues, go to next candidate
        else:
            if pipx_version_result.exit_code == 0:
                return pipx_command
    # If pipx isn't found in global path or python -m pipx then bail out
    #   this is an exceptional circumstance since pipx should always be present with algokit
    #   since it's installed with brew / winget as a dependency, and otherwise is used to install algokit
    raise click.ClickException(error_message)

def get_npm_command(error_message: str, *, is_npx: bool = False) -> list[str]:
    command = "npx" if is_npx else "npm"
    path = shutil.which(command)
    if not path:
        raise click.ClickException(error_message)
        # Create the npm directory inside %APPDATA% if it doesn't exist, as npx on windows needs this.
        # See https://github.com/npm/cli/issues/7089 for more details.
    if is_windows():
        appdata_dir = os.getenv("APPDATA")
        if appdata_dir is not None:
            appdata_dir_path = Path(appdata_dir).expanduser()
            npm_dir = appdata_dir_path / "npm"
            try:
                if not npm_dir.exists():
                    npm_dir.mkdir(parents=True)
            except OSError as ex:
                raise click.ClickException(
                    f"Failed to create the `npm` directory in {appdata_dir_path}.\n"
                    "This command uses `npx`, which requires the `npm` directory to exist "
                    "in the above path, otherwise an ENOENT 4058 error will occur.\n"
                    "Please create this directory manually and try again."
                ) from ex
        return [f"{command}.cmd"]
    return [command]

def get_python_paths() -> Iterator[str]:
    for python_name in ("python3", "python"):
        if python_path := which(python_name):
            yield python_path
    python_base_path = get_base_python_path()
    if python_base_path is not None:
        yield python_base_path

def get_base_python_path() -> str | None:
    this_python: str | None = sys.executable
    if not this_python or this_python.endswith("algokit"):
        # Not: can be empty or None... yikes! unlikely though
        # https://docs.python.org/3.10/library/sys.html#sys.executable
        return None
    # not in venv... not recommended to install algokit this way, but okay
    if sys.prefix == sys.base_prefix:
        return this_python
    this_python_path = Path(this_python)
    # try resolving symlink, this should be default on *nix
    try:
        if this_python_path.is_symlink():
            return str(this_python_path.resolve())
    except (OSError, RuntimeError):
        pass
    # otherwise, try getting an internal value which should be set when running in a .venv
    # this will be the value of `home = <path>` in pyvenv.cfg if it exists
    if base_home := getattr(sys, "_home", None):
        base_home_path = Path(base_home)
        for name in ("python", "python3", f"python3.{sys.version_info.minor}"):
            candidate_path = base_home_path / name
            if is_windows():
                candidate_path = candidate_path.with_suffix(".exe")
            if candidate_path.is_file():
                return str(candidate_path)
    # give up, we tried...
    return this_python

def is_binary_mode() -> bool:
    """
    Check if the current Python interpreter is running in a native binary frozen environment.
    return: True if running in a native binary frozen environment, False otherwise.
    """
    return getattr(sys, "frozen", False) and hasattr(sys, "_MEIPASS")

def is_wsl() -> bool:
    """
    detects if Python is running in WSL
    https://github.com/scivision/detect-windows-subsystem-for-linux
    """
    return platform.uname().release.endswith(("-Microsoft", "microsoft-standard-WSL2"))

def split_command_string(command: str) -> list[str]:
    """
    Parses a command string into a list of arguments, handling both shell and non-shell commands
    """

    if platform.system() == "Windows":
        import mslex

        return mslex.split(command)
    else:
        import shlex

        return shlex.split(command)

def load_env_file(path: Path) -> dict[str, str | None]:
    """Load the general .env configuration.

    Args:
        path (Path): Path to the .env file or directory containing the .env file.

    Returns:
        dict[str, str | None]: Dictionary with .env configurations.
    """

    # Check if the path is a file, if yes, use it directly
    if path.is_file():
        env_path = path
    else:
        # Assume the default .env file name in the given directory
        env_path = path / ".env"

    if env_path.exists():
        return dotenv.dotenv_values(env_path, verbose=True)
    return {}

def get_algorand_client_for_network(network: AlgorandNetwork) -> AlgorandClient:
    from algokit.cli.common.constants import AlgorandNetwork

    match network:
        case AlgorandNetwork.MAINNET:
            return AlgorandClient.mainnet()
        case AlgorandNetwork.TESTNET:
            return AlgorandClient.testnet()
        case AlgorandNetwork.LOCALNET:
            return AlgorandClient.default_localnet()
        case _:
            raise ValueError(f"Unsupported network: {network}")

def _find_puyapy_command_at_version(version: str) -> list[str]:
    """
    Find puyapy command with a specific version.
    If the puyapy version isn't installed, install it with pipx run.
    """
    for puyapy_command in _get_candidate_puyapy_commands():
        try:
            puyapy_version_result = run([*puyapy_command, "--version"])
        except OSError:
            pass  # in case of path/permission issues, go to next candidate
        else:
            if puyapy_version_result.exit_code == 0 and (
                extract_version_triple(version) == extract_version_triple(puyapy_version_result.output)
            ):
                return puyapy_command

    pipx_command = find_valid_pipx_command(
        "Unable to find pipx install so that the `PuyaPy` compiler can be run; "
        "please install pipx via https://pypa.github.io/pipx/ "
        "and then try `algokit compile python ...` again."
    )

    return [
        *pipx_command,
        "run",
        f"--spec=puyapy=={version}",
        "puyapy",
    ]

def _find_puyapy_command() -> list[str]:
    """
    Find puyapy command.
    If puyapy isn't installed, install the latest version with pipx.
    """
    for puyapy_command in _get_candidate_puyapy_commands():
        try:
            puyapy_help_result = run([*puyapy_command, "--version"])
        except OSError:
            pass  # in case of path/permission issues, go to next candidate
        else:
            if puyapy_help_result.exit_code == 0:
                return puyapy_command

    pipx_command = find_valid_pipx_command(
        "Unable to find pipx install so that the `PuyaPy` compiler can be run; "
        "please install pipx via https://pypa.github.io/pipx/ "
        "and then try `algokit compile python ...` again."
    )
    return [
        *pipx_command,
        "run",
        "--spec=puyapy",
        "puyapy",
    ]

def _find_project_puyats_command(
    npm_command: list[str], npx_command: list[str], version: str | None
) -> list[str] | None:
    """
    Try to find PuyaTs command installed at the project level.
    """
    try:
        result = run([*npm_command, "ls", "--no-unicode"])
        # Normally we would check the exit code, however `npm ls` may return a non zero exit code
        # when certain dependencies are not met. We still want to continue processing.
        if result.output != "":
            compile_command = [*npx_command, PUYATS_NPM_PACKAGE]
            for line in result.output.splitlines():
                if PUYATS_NPM_PACKAGE in line:
                    if "UNMET DEPENDENCY" in line:
                        raise ModuleNotFoundError(
                            f"{PUYATS_NPM_PACKAGE} was detected in the project, but is not installed."
                        )
                    if version is not None:
                        installed_version = extract_semantic_version(line)
                        if version == installed_version:
                            return compile_command
                    else:
                        return compile_command
    except OSError:
        pass
    except ValueError:
        pass

    return None

def _find_global_puyats_command(
    npm_command: list[str], npx_command: list[str], version: str | None
) -> list[str] | None:
    """
    Try to find PuyaTs command installed globally.
    """
    return _find_project_puyats_command([*npm_command, "--global"], npx_command, version)

