def approval_program():
    # Define global state keys
    global_counter = Bytes("Counter")

    on_create = Seq([
        App.globalPut(global_counter, Int(0)),
        Return(Int(1))
    ])

    on_call_increment = Seq([
        App.globalPut(global_counter, App.globalGet(global_counter) + Int(1)),
        Return(Int(1))
    ])

    program = Cond(
        [Txn.application_id() == Int(0), on_create],  # App creation
        [Txn.on_completion() == OnComplete.NoOp, on_call_increment]  # NoOp call: increment counter
    )

    return program

