    # Remove 'https://' or 'http://'
    if url.startswith('https://'):
        url = url[8:]
    elif url.startswith('http://'):
        url = url[7:]
    index = url.rfind('/')
    extension = ''
    if index != -1:
        index2 = url.rfind('.')
        if index2 > index:
            extension = url[index2+1:].lower()
            for ext in extensions:
                if extension.startswith(ext):
