        url = url[4:]
    return url

def is_still_valid(url):
    # se è vuoto ritorna False
    if url == '':
        return False
    # se non ha un punto ritorna False
    if '.' not in url:
        return False
    # se ha due punti consecutivi ritorna False
    if '..' in url:
        return False
    return True

