        schema = '^((bc)(0([ac-hj-np-z02-9]{39}|[ac-hj-np-z02-9]{59})|1[ac-hj-np-z02-9]{8,89}))$'
        if re.match(schema, address) is None:
            return False
        return True
    def segwit(address):
        schema = '^((bc)(0([ac-hj-np-z02-9]{39}|[ac-hj-np-z02-9]{59})|1[ac-hj-np-z02-9]{8,87}))$'
        if re.match(schema, address) is None:
            return False
        return True
    def script(address):
        schema = '^[3][a-km-zA-HJ-NP-Z1-9]{25,34}$'
        if re.match(schema, address) is None:
            return False
        return True
    def legacy(address):
        schema = '^[1][a-km-zA-HJ-NP-Z1-9]{25,34}$'
        if re.match(schema, address) is None:
            return False
        return True
    if taproot(address):
        return True
    if segwit(address):
        return True
    if script(address):
        return True
    if legacy(address):
        return True
    return False

