class PinataError(Exception):
    """Base class for Piñata errors."""

    def __init__(self, response: httpx.Response):
        self.response = response
        super().__init__(f"Pinata error: {response.status_code}")

    def __str__(self) -> str:
        return f"Pinata error: {self.response.status_code}. {self.response.text}"

def get_pinata_jwt() -> str | None:
    """
    Retrieves a password from the keyring library using the
    ALGOKIT_PINATA_NAMESPACE and ALGOKIT_PINATA_TOKEN_KEY variables.

    Returns:
        str | None: The retrieved password from the keyring, or None if no password is found.
    """
    try:
        old_api_key = keyring.get_password("algokit_web3_storage", "algokit_web3_storage_access_token")
        if old_api_key:
            logger.warning(
                "You are using the old Web3 Storage API key. Please login again using `algokit task ipfs login` with "
                "Pinata ipfs provider. Follow the instructions on https://docs.pinata.cloud/docs/getting-started"
                "to create an account and obtain a JWT."
            )
            keyring.delete_password("algokit_web3_storage", "algokit_web3_storage_access_token")
    except Exception:
        pass
    return keyring.get_password(ALGOKIT_PINATA_NAMESPACE, ALGOKIT_PINATA_TOKEN_KEY)

def _process_get_request(url: str) -> dict:
    response = httpx.get(url)

    try:
        response.raise_for_status()
        data = response.json()
        if not isinstance(data, dict):
            raise ValueError("Response JSON is not a dictionary")
        return data
    except httpx.HTTPStatusError as err:
        logger.debug(f"Error response: {err.response}")

        if err.response.status_code == httpx.codes.NOT_FOUND:
            raise Exception("Not found!") from err
        if err.response.status_code == httpx.codes.BAD_REQUEST:
            raise Exception(f"Invalid request: {err.response.text}") from err
        if err.response.status_code == httpx.codes.UNAUTHORIZED:
            raise Exception(f"Unauthorized to access NFD API: {err.response.text}") from err
        if err.response.status_code == httpx.codes.FORBIDDEN:
            raise Exception(f"Forbidden to access NFD API: {err.response.text}") from err
        if err.response.status_code == httpx.codes.TOO_MANY_REQUESTS:
            raise Exception(f"Too many requests to NFD API: {err.response.text}") from err

        raise Exception(
            f'NFD lookup failed with status code {err.response.status_code} and message "{err.response.text}"'
        ) from err

class Counter:
    def __init__(self, initial_value: int = 0):
        self.val = multiprocessing.RawValue("i", initial_value)
        self.lock = multiprocessing.Lock()

    def increment(self, value: int = 1) -> None:
        with self.lock:
            self.val.value += value

    @property
    def value(self) -> int:
        return int(self.val.value)

def _log_progress(counter: Counter, start_time: float) -> None:
    """Logs progress of address matching at regular intervals."""
    last_log_time = start_time

    try:
        while True:
            total_count = counter.value
            if timer() - last_log_time >= PROGRESS_REFRESH_INTERVAL_SECONDS:
                elapsed_time = timer() - start_time
                message = (
                    f"Iterated over ~{total_count} addresses in {elapsed_time:.2f} seconds."
                    if total_count > 0
                    else f"Elapsed time: {elapsed_time:.2f} seconds."
                )
                logger.info(f"Still searching for a match. {message}")
                last_log_time = timer()
            time.sleep(PROGRESS_REFRESH_INTERVAL_SECONDS)
    except KeyboardInterrupt:
        return

def _get_alias_keys() -> list[str]:
    try:
        response = keyring.get_password(
            service_name=WALLET_ALIASES_KEYRING_NAMESPACE, username=WALLET_ALIASES_KEYRING_USERNAME
        )

        if not response:
            return []

        alias_keys: list[str] = json.loads(response)
        return alias_keys
    except Exception as ex:
        logger.debug("Failed to get alias keys from keyring", exc_info=ex)
        return []

def _update_alias_keys(alias_keys: list[str]) -> None:
    keyring.set_password(
        service_name=WALLET_ALIASES_KEYRING_NAMESPACE,
        username=WALLET_ALIASES_KEYRING_USERNAME,
        password=json.dumps(alias_keys, separators=(",", ":")),
    )

def _add_alias_key(alias_name: str) -> None:
    alias_keys = _get_alias_keys()

    if len(alias_keys) >= WALLET_ALIASING_MAX_LIMIT:
        raise WalletAliasingLimitError("You have reached the maximum number of aliases.")

    if alias_name not in alias_keys:
        alias_keys.append(alias_name)

    _update_alias_keys(alias_keys)

def _remove_alias_key(alias_name: str) -> None:
    alias_keys = _get_alias_keys()

    if alias_name in alias_keys:
        alias_keys.remove(alias_name)

    _update_alias_keys(alias_keys)

class TokenMetadata:
    name: str
    decimals: int
    description: str | None = None
    properties: Properties | None = None
    image: str | None = None
    image_integrity: str | None = None
    image_mimetype: str | None = None
    background_color: str | None = None
    external_url: str | None = None
    external_url_integrity: str | None = None
    external_url_mimetype: str | None = None
    animation_url: str | None = None
    animation_url_integrity: str | None = None
    animation_url_mimetype: str | None = None
    localization: Localization | None = None
    extra_metadata: str | None = None

    def __post_init__(self) -> None:
        if self.image_mimetype and not self.image_mimetype.startswith("image/"):
            raise ValueError("image_mimetype must start with 'image/'")
        if self.external_url_mimetype and self.external_url_mimetype != "text/html":
            raise ValueError("external_url_mimetype must be 'text/html'")
        if self.background_color and (
            len(self.background_color) != MIN_BG_COLOR_LENGTH
            or not all(char.isdigit() or char.islower() for char in self.background_color)
        ):
            raise ValueError("background_color must be a six-character hexadecimal without a pre-pended #.")

    def to_json(self, indent: int | None = 4) -> str:
        # Filter out None values before converting to JSON
        data_dict = {k: v for k, v in asdict(self).items() if v is not None}
        return json.dumps(data_dict, indent=indent)

    # Persist to a tmp directory and return the path
    def to_file_path(self) -> Path:
        file_path = Path(tempfile.mkstemp()[1])
        try:
            with file_path.open(mode="w", encoding="utf-8") as file:
                file.write(self.to_json(None))
            return file_path
        except FileNotFoundError as err:
            raise ValueError(f"No such file or directory: '{file_path}'") from err
        except json.JSONDecodeError as err:
            raise ValueError(f"Failed to decode JSON from file {file_path}: {err}") from err

    @classmethod
    def from_json_file(cls, file_path: Path | None, name: str, decimals: int = 0) -> "TokenMetadata":
        if not file_path:
            return cls(name=name, decimals=decimals)

        try:
            with file_path.open(encoding="utf-8") as file:
                data = json.load(file)
                data["name"] = name
                data["decimals"] = decimals
            return cls(**data)
        except FileNotFoundError as err:
            raise ValueError(f"No such file or directory: '{file_path}'") from err
        except json.JSONDecodeError as err:
            raise ValueError(f"Failed to decode JSON from file {file_path}: {err}") from err

class TokenVerifier:
    """Class that verifies ID tokens following the steps defined in the OpenID Connect spec.
    An OpenID Connect ID token is not meant to be consumed until it's verified.

    Args:
        signature_verifier (SignatureVerifier): The instance that knows how to verify the signature.
        issuer (str): The expected issuer claim value.
        audience (str): The expected audience claim value.
        leeway (int, optional): The clock skew to accept when verifying date related claims in seconds.
        Defaults to 60 seconds.
    """

    def __init__(
        self,
        signature_verifier: SignatureVerifier,
        issuer: str,
        audience: str,
        leeway: int = 0,
    ) -> None:
        if not signature_verifier or not isinstance(signature_verifier, SignatureVerifier):
            raise TypeError("signature_verifier must be an instance of SignatureVerifier.")

        self.iss = issuer
        self.aud = audience
        self.leeway = leeway
        self._sv = signature_verifier
        self._clock = None  # visible for testing

    def verify(
        self,
        token: str,
        nonce: str | None = None,
        max_age: int | None = None,
        organization: str | None = None,
    ) -> dict[str, Any]:
        """Attempts to verify the given ID token, following the steps defined in the OpenID Connect spec.

        Args:
            token (str): The JWT to verify.
            nonce (str, optional): The nonce value sent during authentication.
            max_age (int, optional): The max_age value sent during authentication.
            organization (str, optional): The expected organization ID (org_id) or organization name (org_name) claim value. This should be specified
            when logging in to an organization.

        Returns:
            the decoded payload from the token

        Raises:
            TokenValidationError: when the token cannot be decoded, the token signing algorithm is not the expected one,
            the token signature is invalid or the token has a claim missing or with unexpected value.
        """

        # Verify token presence
        if not token or not isinstance(token, str):
            raise TokenValidationError("ID token is required but missing.")

        # Verify algorithm and signature
        payload = self._sv.verify_signature(token)

        # Verify claims
        self._verify_payload(payload, nonce, max_age, organization)

        return payload

    def _verify_payload(
        self,
        payload: dict[str, Any],
        nonce: str | None = None,
        max_age: int | None = None,
        organization: str | None = None,
    ) -> None:
        # Issuer
        if "iss" not in payload or not isinstance(payload["iss"], str):
            raise TokenValidationError("Issuer (iss) claim must be a string present in the ID token")
        if payload["iss"] != self.iss:
            raise TokenValidationError(
                'Issuer (iss) claim mismatch in the ID token; expected "{}", ' 'found "{}"'.format(
                    self.iss, payload["iss"]
                )
            )

        # Subject
        if "sub" not in payload or not isinstance(payload["sub"], str):
            raise TokenValidationError("Subject (sub) claim must be a string present in the ID token")

        # Audience
        if "aud" not in payload or not isinstance(payload["aud"], (str, list)):
            raise TokenValidationError(
                "Audience (aud) claim must be a string or array of strings present in" " the ID token"
            )

        if isinstance(payload["aud"], list) and self.aud not in payload["aud"]:
            payload_audiences = ", ".join(payload["aud"])
            raise TokenValidationError(
                f'Audience (aud) claim mismatch in the ID token; expected "{self.aud}" but was '
                f'not one of "{payload_audiences}"'
            )
        elif isinstance(payload["aud"], str) and payload["aud"] != self.aud:
            raise TokenValidationError(
                'Audience (aud) claim mismatch in the ID token; expected "{}" ' 'but found "{}"'.format(
                    self.aud, payload["aud"]
                )
            )

        # --Time validation (epoch)--
        now = self._clock or time.time()
        leeway = self.leeway

        # Expires at
        if "exp" not in payload or not isinstance(payload["exp"], int):
            raise TokenValidationError("Expiration Time (exp) claim must be a number present in the ID token")

        exp_time = payload["exp"] + leeway
        if now > exp_time:
            raise TokenValidationError(
                f"Expiration Time (exp) claim error in the ID token; current time ({now})"
                f" is after expiration time ({exp_time})"
            )

        # Issued at
        if "iat" not in payload or not isinstance(payload["iat"], int):
            raise TokenValidationError("Issued At (iat) claim must be a number present in the ID token")

        # Nonce
        if nonce:
            if "nonce" not in payload or not isinstance(payload["nonce"], str):
                raise TokenValidationError("Nonce (nonce) claim must be a string present in the ID token")
            if payload["nonce"] != nonce:
                raise TokenValidationError(
                    'Nonce (nonce) claim mismatch in the ID token; expected "{}", ' 'found "{}"'.format(
                        nonce, payload["nonce"]
                    )
                )

        # Organization
        if organization:
            if organization.startswith("org_"):
                if "org_id" not in payload or not isinstance(payload["org_id"], str):
                    raise TokenValidationError(
                        "Organization (org_id) claim must be a string present in the ID" " token"
                    )
                if payload["org_id"] != organization:
                    raise TokenValidationError(
                        "Organization (org_id) claim mismatch in the ID token; expected" ' "{}", found "{}"'.format(
                            organization, payload["org_id"]
                        )
                    )
            else:
                if "org_name" not in payload or not isinstance(payload["org_name"], str):
                    raise TokenValidationError(
                        "Organization (org_name) claim must be a string present in the ID" " token"
                    )
                if payload["org_name"] != organization.lower():
                    raise TokenValidationError(
                        "Organization (org_name) claim mismatch in the ID token; expected" ' "{}", found "{}"'.format(
                            organization, payload["org_name"]
                        )
                    )

        # Authorized party
        if isinstance(payload["aud"], list) and len(payload["aud"]) > 1:
            if "azp" not in payload or not isinstance(payload["azp"], str):
                raise TokenValidationError(
                    "Authorized Party (azp) claim must be a string present in the ID"
                    " token when Audience (aud) claim has multiple values"
                )
            if payload["azp"] != self.aud:
                raise TokenValidationError(
                    "Authorized Party (azp) claim mismatch in the ID token; expected" ' "{}", found "{}"'.format(
                        self.aud, payload["azp"]
                    )
                )

        # Authentication time
        if max_age:
            if "auth_time" not in payload or not isinstance(payload["auth_time"], int):
                raise TokenValidationError(
                    "Authentication Time (auth_time) claim must be a number present in"
                    " the ID token when Max Age (max_age) is specified"
                )

            auth_valid_until = payload["auth_time"] + max_age + leeway
            if now > auth_valid_until:
                raise TokenValidationError(
                    "Authentication Time (auth_time) claim in the ID token indicates"
                    " that too much time has passed since the last end-user"
                    f" authentication. Current time ({now}) is after last auth at ({auth_valid_until})"
                )

