from pyteal import *
def governance_rewards_contract():
    committed_amount = Btoi(App.globalGet(Bytes("committed")))
    reward = ScratchVar(TealType.uint64)

    return Seq([
        If(committed_amount <= Int(5000000)).Then(
            reward.store(committed_amount * Int(20) / Int(100))
        ).Elif(committed_amount <= Int(10000000)).Then(
            reward.store(committed_amount * Int(15) / Int(100))
        ).Elif(committed_amount <= Int(12000000)).Then(
            reward.store(committed_amount * Int(125) / Int(1000))
        ).Else(
            reward.store(Int(0))
        ),
        App.globalPut(Bytes("reward"), reward.load()),
        Return(Int(1))
    ])
if __name__ == "__main__":
    print(compileTeal(governance_rewards_contract(), mode=Mode.Application))

