from pyteal import *

def approval_program():
    handle_creation = Seq([
        Approve()
    ])

    handle_deletion = Seq([
        # Only allow the creator to delete the app
        Assert(Txn.sender() == Global.creator_address()),
        Approve()
    ])

    handle_noop = Seq([
        Approve()
    ])

    program = Cond(
        [Txn.application_id() == Int(0), handle_creation],
        [Txn.on_completion() == OnComplete.DeleteApplication, handle_deletion],
        [Txn.on_completion() == OnComplete.NoOp, handle_noop],
        [Txn.on_completion() == OnComplete.UpdateApplication, Reject()],
        [Txn.on_completion() == OnComplete.CloseOut, Approve()],
        [Txn.on_completion() == OnComplete.OptIn, Approve()],
    )

    return compileTeal(program, mode=Mode.Application, version=6)

def clear_state_program():
    return compileTeal(Approve(), mode=Mode.Application, version=6)

print(approval_program())
print(clear_state_program())

