def _get_algokit_version_output() -> DoctorResult:
    current = get_current_package_version()
    try:
        latest = get_latest_github_version()
    except Exception as ex:
        logger.warning("Failed to check latest AlgoKit release version", exc_info=ex)
        latest = None
    if latest is None or current == latest:
        output = current
    else:
        output = click.style(current, fg=WARNING_COLOR) + f" (latest: {latest})"
    return DoctorResult(ok=True, output=output)

def _get_production_dependencies() -> dict[str, str]:
    """Gets versions of all direct production dependencies."""
    try:
        import importlib.metadata
        import re

        # Get package dependencies from metadata
        dist = importlib.metadata.distribution("algokit")
        requires = dist.requires or []

        result = {}
        for req in requires:
            if match := re.match(r"^([A-Za-z0-9_\-\.]+)", req):
                dep = match.group(1)
                try:
                    result[dep] = importlib.metadata.version(dep)
                except importlib.metadata.PackageNotFoundError:
                    result[dep] = "Not installed"

        return result

    except Exception:
        return {"Error": "Could not retrieve dependencies"}

class NetworkConfigurationRequired(TypedDict):
    algod_url: str
    indexer_url: str

def get_explore_url(network: str) -> str:
    if network == "localnet" and NETWORKS[network].get("algod_url") != DEFAULT_ALGOD_SERVER:
        query_string = urlencode(
            [
                (key, value)
                for key, value in NETWORKS[network].items()
                if key in ["algod_url", "algod_port", "indexer_url", "indexer_port", "kmd_url", "kmd_port"]
            ]
        )
        return f"{get_algokit_url(network)}?{query_string}"

    return get_algokit_url(network)

def explore_command(network: str) -> None:
    url = get_explore_url(network)
    logger.info(f"Opening {network} explorer in your default browser")

    if is_wsl():
        import webbrowser

        warning = (
            "Unable to open browser from WSL environment.\n"
            "Ensure 'wslu' is installed: (https://wslutiliti.es/wslu/install.html),\n"
            f"or open the URL manually: '{url}'."
        )
        try:
            if not webbrowser.open(url):
                logger.warning(warning)
        except Exception as e:
            logger.warning(warning, exc_info=e)
    else:
        click.launch(url)

class GeneratorGroup(click.Group):
    def get_command(self, ctx: click.Context, cmd_name: str) -> click.Command | None:
        return_value = super().get_command(ctx, cmd_name)

        if return_value is not None:
            return return_value

        commands = _load_custom_generate_commands(Path.cwd())
        return commands.get(cmd_name)

    def list_commands(self, ctx: click.Context) -> list[str]:
        predefined_command_names = super().list_commands(ctx)
        dynamic_commands = _load_custom_generate_commands(Path.cwd())
        dynamic_command_names = list(dynamic_commands)

        return sorted(predefined_command_names + dynamic_command_names)

def generate_client(
    output_path_pattern: str | None, app_spec_path_or_dir: Path, language: str | None, version: str | None
) -> None:
    """Create a typed ApplicationClient from an ARC-32/56 application.json

    Supply the path to an application specification file or a directory to recursively search
    for "application.json" files"""
    if language is not None:
        generator = ClientGenerator.create_for_language(language, version)
    elif output_path_pattern is not None:
        extension = Path(output_path_pattern).suffix
        try:
            generator = ClientGenerator.create_for_extension(extension, version)
        except KeyError as ex:
            raise click.ClickException(
                "Could not determine language from file extension, Please use the --language option to specify a "
                "target language"
            ) from ex
    else:
        raise click.ClickException(
            "One of --language or --output is required to determine the client language to generate"
        )

    try:
        generator.generate_all(
            app_spec_path_or_dir,
            output_path_pattern,
            raise_on_path_resolution_failure=False,
        )
    except AppSpecsNotFoundError as ex:
        raise click.ClickException("No app specs found") from ex

